export {}

declare global {
    /**
     * The Blob object represents a blob, which is a file-like object of immutable, raw data.
     * Access to the raw data is not allowed.
     */
    interface Blob {
        /**
         * Readonly: The size of data contained in the Blob object. 
         * Note: This is set to "-1". 
         */
        size: number;
        /**
         * Readonly: A string indicating the MIME type of the data contained in the Blob. 
         * Note: This is set to "application/octet-stream".
         */
        type: string;
    }
    
    const Blob: {
        prototype: Blob;
        new(): Blob;
    }

    /**
     * The Attachment object represents an Attachment, which is a file-like object of immutable, raw data.
     */
    interface Attachment {
        /**
         * Returns the name of the file referenced by the Attachment object.
         */
        filename: string;
        /**
         * Represents the Blob content of the Attachment object.
         * Note: The Blob content is hidden and not accessible.
         */
        content: Blob;
    }
}