// Force TS to interpret this file as a module.
export {}

declare global {

    /**
     * Metadata for a JavaScript broker.
     */
    interface BrokerMetadata {
        /**
         * The system name for the JavaScript broker.
         */
        systemName: string,
        /**
         * The display name for the JavaScript broker.
         */
        displayName: string,
        /**
         * The description for the JavaScript broker.
         */
        description: string,
        /**
         * The configuration settings
         */
        configuration?: {[name: string]: BrokerConfiguration}
    }

    /**
     * Represents a configuration setting
     */
    interface BrokerConfiguration {
        /**
         * The display name for a configuration setting
         */
        displayName: string,
        /**
         * The type of the configuration setting
         */
        type: ServiceValueType,
        /**
         * The extended type for a configuration setting
         */
        extendedType?: ExtendedType,
        /**
         * Whether or not the configuration setting is requiured
         */
        required?: boolean,
        /**
         * Whether or not the configuration setting is protected
         */
        protected?: boolean,
        /**
         * The initial value of the configuration setting
         */
        value?: any
    }

    /**
     * The {@link BrokerMetadata} for the current broker.
     */
    let metadata: BrokerMetadata;

}
